import React, {useState} from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';

const Expenses = (props) => {
  const [selectedYear, setSelectedYear] = useState('2019');
  const [currentExpenses, setCurrentExpenses] = useState(props.items)
  const saveSelectedExpenseYearHandler = (enteredYearSelectedData) => {
    console.log(enteredYearSelectedData)
    setSelectedYear(enteredYearSelectedData);
    setCurrentExpenses((prevExpenses) => {
      const filteredList = prevExpenses.filter( expense => expense.id == selectedYear)
      console.log(filteredList)
      return [filteredList]
    });
  };

  return (
    <div>
    <Card className="expenses">
    <ExpensesFilter selected={selectedYear} onSaveSelectedExpenseYear={saveSelectedExpenseYearHandler}/>
    {currentExpenses.map((expense) =>  <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      /> )}
      {/* <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
